OnPermissionRequest = (request, invocation) =>
{
    Console.WriteLine($"[Permission] Kind: {request.Kind}");
    return Task.FromResult(
        new PermissionRequestResult { Kind = "approved" });
}

OnPermissionRequest = (request, invocation) =>
{
    Console.WriteLine($"[Permission] DENIED: {request.Kind}");
    return Task.FromResult(
        new PermissionRequestResult
        { Kind = "denied-interactively-by-user" });
}

OnPermissionRequest = async (request, invocation) =>
{
    await Task.Delay(500); // Simulate async check
    return new PermissionRequestResult { Kind = "approved" };
}

OnUserInputRequest = (request, invocation) =>
{
    Console.WriteLine($"Question: {request.Question}");
    if (request.Choices is { Count: > 0 })
    {
        Console.WriteLine($"Choices: [{string.Join(", ", request.Choices)}]");
        return Task.FromResult(new UserInputResponse
        {
            Answer = request.Choices[0],
            WasFreeform = false
        });
    }
    return Task.FromResult(new UserInputResponse
    {
        Answer = "default answer",
        WasFreeform = true
    });
}

return Task.FromResult(new UserInputResponse
{
    Answer = "My favorite color is emerald green!",
    WasFreeform = true
});

